export const loadBooks = (payload) => {
    return (dispatch, getState) => {
        fetch()
    }
    return {
        type: 'LOAD_BOOK',
        payload: payload
    }
}
export const addToReadingList = (payload) => {
    return {
        type: 'ADD_TO_READING_LIST',
        payload: payload
    }
}

export const removeFromReadingList = (payload) => {
    return {
        type: 'REMOVE_FROM_READING_LIST',
        payload: payload
    }
}
